UPDATE_SQL:
update xip_pub_mnu_group
   set MNU_GROUP_NAME =${MNU_GROUP_NAME},
       MNU_GROUP_DESC =${MNU_GROUP_DESC}
 where MNU_GROUP_CODE =${MNU_GROUP_CODE}

update XIP_PUB_VAL_SET_LANGUAGE
set VAL_SET_DESC=${VAL_SET_DESC},VAL_SET_NAME=${VAL_SET_NAME}
where VAL_LANG=${VAL_LANG}
  and VAL_SET_CODE=${VAL_SET_CODE}

update xip_pub_val_sets
set VAL_SET_NAME =${VAL_SET_NAME},VAL_SET_DESC=${VAL_SET_DESC},VAL_SET_TYPE=${VAL_SET_TYPE},ENABLE_FLAG=${ENABLE_FLAG},LAST_UPDATE_DATE=${sys.date}
where VAL_SET_CODE=${VAL_SET_CODE}

update xip_pub_mnu_group
   set MNU_GROUP_NAME =${MNU_GROUP_NAME},
       MNU_GROUP_DESC =${MNU_GROUP_DESC}
 where MNU_GROUP_CODE =${MNU_GROUP_CODE}

update xip_pub_val_set_dtl
SET VAL_NAME=${VAL_NAME},
    DESCRIPTION=${DESCRIPTION},
    ENABLE_FLAG=${ENABLE_FLAG},
    LAST_UPDATE_DATE=${sys.date}
where VAL_SET_CODE=${VAL_SET_CODE}
  and VAL_CODE=${VAL_CODE}

UPDATE XC_GL_PR_MODES
   SET MODE_TYPE        = ${MODE_TYPE},
       MODE_NAME        = ${MODE_NAME},
       MODE_DESC        = ${MODE_DESC},
       IS_ENABLED       = ${IS_ENABLED}
 WHERE MODE_ID = ${MODE_ID}

UPDATE XC_GL_V_SOURCE
   SET V_SRC_URL        = ${V_SRC_URL},
       V_SRC_DESC       = ${V_SRC_DESC},
       IS_SYS           = ${IS_SYS},
       V_CAT            = ${V_CAT}
 WHERE V_SRC_CODE = ${V_SRC_CODE}

update xip_pub_messages
set MES_CONTENT = ${MES_CONTENT}
where MES_CODE = ${MES_CODE} and MES_LANG = ${MES_LANG}

update xip_pub_param_vals
set VAL=${VAL},
    ENABLE_FLAG=${ENABLE_FLAG},
    LAST_UPDATE_DATE=${sys.date}
where PARAM_ID=(select pa.PARAM_ID from xip_pub_params pa where pa.PARAM_CODE = ${PARAM_CODE})
  and VAL_LEVEL=${VAL_LEVEL}
  and TARGET_ID=${TARGET_ID}

update xip_portal_language
set LANG_VAL=${LANG_VAL},LANG_DESC=${LANG_DESC}
where PORTAL_ID = ${PORTAL_ID}
  and LANG_CODE = ${LANG_CODE}
  and PORTAL_TYPE = ${PORTAL_TYPE}

UPDATE XC_GL_V_CATEGORY
   SET V_CATEGORY_CODE = ${V_CATEGORY_CODE},
       V_CATEGORY_NAME = ${V_CATEGORY_NAME},
       V_CATEGORY_DESC = ${V_CATEGORY_DESC}
 WHERE V_CATEGORY_ID = ${V_CATEGORY_ID}

update xip_pub_val_sets
set VAL_SET_NAME =${VAL_SET_NAME},VAL_SET_DESC=${VAL_SET_DESC},VAL_SET_TYPE=${VAL_SET_TYPE},ENABLE_FLAG=${ENABLE_FLAG},LAST_UPDATE_DATE=${sys.date}
where VAL_SET_CODE=${VAL_SET_CODE}

UPDATE xip_pub_fun_tree
SET  UP_ID=(select temp.FUN_TREE_ID  from(select FUN_TREE_ID from xip_pub_fun_tree where FUN_TREE_CODE = ${UP_CODE})  temp)
WHERE FUN_TREE_CODE=${FUN_TREE_CODE}

UPDATE XC_GL_TEMPLATE
   SET TEMPLATE_CODE    = ${TEMPLATE_CODE},
       TEMPLATE_NAME    = ${TEMPLATE_NAME},
       TEMPLATE_DESC    = ${TEMPLATE_DESC},
       APP_ID           = ${APP_ID}
 WHERE TEMPLATE_ID = ${TEMPLATE_ID}

update xip_pub_app_language
set APP_LANG_VAL=${APP_LANG_VAL}
where APP_CODE = ${APP_CODE}
   and APP_LANG = ${APP_LANG}

UPDATE XC_GL_CURRENCIES
   SET CURRENCY_NAME    = ${CURRENCY_NAME},
       NATION           = ${NATION},
       IS_ENABLED       = ${IS_ENABLED},
       PERICISON        = ${PERICISON}
 WHERE CURRENCY_CODE = ${CURRENCY_CODE}

 UPDATE XC_GL_DIMENSIONS
  SET DIM_CODE         = ${DIM_CODE},
      DIM_NAME         = ${DIM_NAME},
      DIM_TYPE         = ${DIM_TYPE},
      DIM_DESC         = ${DIM_DESC}
WHERE DIM_ID = ${DIM_ID}

UPDATE XC_GL_ACC_GROUP
   SET ACC_GROUP_CODE   = ${ACC_GROUP_CODE},
       ACC_GROUP_NAME   = ${ACC_GROUP_NAME},
       ACC_GROUP_DESC   = ${ACC_GROUP_DESC},
       ORDERBY          = ${ORDERBY}
 WHERE ACC_GROUP_ID = ${ACC_GROUP_ID}

update XIP_PUB_ROLE_LANGUAGE
set ROLE_NAME_LANG_VAL=${ROLE_NAME_LANG_VAL},
     ROLE_DESC_LANG_VAL=${ROLE_DESC_LANG_VAL}
where ROLE_CODE=${ROLE_CODE}
   and ROLE_LANG=${ROLE_LANG}

UPDATE 
  XIP_PUB_MENUGROUP_LANGUAGE 
SET
  FUN_LANG_VAL = ${FUN_LANG_VAL} 
WHERE MNU_ID = (SELECT t.MNU_ID FROM xip_pub_mnu_tree t WHERE t.MNU_TREE_CODE = ${MNU_TREE_CODE} AND t.MNU_GROUP_ID = (SELECT p.MNU_GROUP_ID FROM xip_pub_mnu_group p WHERE p.MNU_GROUP_CODE = ${MNU_GROUP_CODE}))
  AND MNU_GROUP_ID = (SELECT mp.MNU_GROUP_ID FROM xip_pub_mnu_group mp WHERE mp.MNU_GROUP_CODE = ${MNU_GROUP_CODE}) 
  AND FUN_CODE = ${FUN_CODE} 
  AND FUN_LANG = ${FUN_LANG}

