UPDATE XC_GL_RULES
   SET RULE_DESC        = ${RULE_DESC},
       FIXED_PREFIX     = ${FIXED_PREFIX},
       SERIAL_LENGTH    = ${SERIAL_LENGTH},
       SERIAL_CATEGORY  = ${SERIAL_CATEGORY}
