update xip_pub_param_language
set PARAM_NAME = ${PARAM_NAME},DESCRIPTION=${DESCRIPTION}
where PARAM_CODE = ${PARAM_CODE}
  and LANG_CODE =${LANG_CODE}

update XIP_PUB_VAL_SET_DTL_LANG
set VAL_NAME=${VAL_NAME},
