UPDATE XIP_PUB_ATT_CAT_SEC
   SET ATT_CAT_ID        = ${ATT_CAT_ID},
       ORG_ID            = ${ORG_ID},
       ATT_SERVER_ID     = ${ATT_SERVER_ID},
       ENABLE_FLAG       = ${ENABLE_FLAG}
 WHERE CAT_SEC_ID = ${CAT_SEC_ID}

update XIP_PUB_FUN_LANGUAGE
     set FUN_LANG_VAL=${FUN_LANG_VAL}
where FUN_CODE = ${FUN_CODE}
   and FUN_LANG = ${FUN_LANG}

UPDATE xip_pub_fun_tree
